css:
:root{
  --ink:#111;
  --line:#111;
  --bg:#ffffff;
  --pad:14px;
  --gap:14px;
}

/* ================= RESET ================= */
*{
  box-sizing:border-box;
  margin:0;
  padding:0;
}

body{
  background:#f4f6f8;
  font-family:Segoe UI, Roboto, Arial, sans-serif;
  font-size:14px;
  padding:20px;
  color:var(--ink);
}

/* ================= HOJA ================= */
.sheet{
  max-width:1200px;
  margin:auto;
  background:var(--bg);
  border:2px solid var(--line);
  padding:16px;
}

/* ================= UTILIDADES ================= */
.row{
  display:grid;
  gap:var(--gap);
}

.center{text-align:center;}
.right{text-align:right;}
.mt-small{margin-top:8px;}

.box{
  border:2px solid var(--line);
  background:white;
}

.box.pad{
  padding:var(--pad);
}

/* ================= HEADER ================= */
.header{
  grid-template-columns:180px 1fr 180px;
  align-items:center;
}

.logo-placeholder{
  height:70px;
  display:flex;
  align-items:center;
  justify-content:center;
  font-weight:bold;
  font-size:12px;
}

.icon-box{
  margin-left:auto;
  margin-bottom:10px;
}

.title{
  font-size:20px;
  font-weight:800;
  line-height:1.2;
}

.subtitle{
  font-size:16px;
  font-weight:700;
}

/* ================= AÑO FISCAL ================= */
.fiscal{
  width:100%;
  border-collapse:collapse;
  font-weight:700;
}

.fiscal td{
  border:2px solid var(--line);
  padding:8px;
}

.fiscal td:last-child{
  width:80px;
  text-align:center;
  font-weight:900;
}

/* ================= PROGRAMA ================= */
.mid{
  grid-template-columns:1fr 1fr;
}

.label{
  font-weight:700;
  margin-bottom:6px;
}

.mid-table{
  width:100%;
  border-collapse:collapse;
}

.mid-table th,
.mid-table td{
  border:2px solid var(--line);
  padding:10px;
}

.mid-table th{
  font-weight:800;
}

.cell-small{
  width:180px;
  height:44px;
}

.input-line{
  height:44px;
}

/* ================= ENTE PUBLICO ================= */
.ente{
  width:100%;
  border-collapse:collapse;
  font-weight:700;
}

.ente td{
  border:2px solid var(--line);
  padding:10px;
}

.ente .value{
  text-align:center;
  font-weight:500;
}

.ente .no{
  width:120px;
  text-align:center;
  font-weight:800;
}

/* ================= PROGRAMA ANUAL ================= */
.bottom{
  grid-template-columns:1.1fr 1.4fr;
}

.programa-grid{
  display:grid;
  grid-template-columns:120px 1fr;
  height:100%;
}

.tag{
  border-right:2px solid var(--line);
  display:flex;
  align-items:center;
  justify-content:center;
  font-weight:900;
}

.programa{
  text-align:center;
  padding:12px;
}

.programa div{
  font-weight:900;
  font-size:16px;
  margin:6px 0;
}

/* ================= RESPONSIVE ================= */
@media (max-width:900px){

  .header{
    grid-template-columns:1fr;
  }

  .mid{
    grid-template-columns:1fr;
  }

  .bottom{
    grid-template-columns:1fr;
  }

}
